import { Component, OnInit, ViewChild, ElementRef, Renderer2 } from '@angular/core';

@Component({
  selector: 'app-angel',
  templateUrl: './angel.component.html',
  styleUrls: ['./angel.component.scss']
})
export class AngelComponent implements OnInit {

  @ViewChild('myModal') myModal: ElementRef;
  @ViewChild('video') video: ElementRef;
  videoUrl: string = "https://www.youtube.com/embed/bKYt8EiqRY0";

  related = [
    {
      image: '/assets/features/mb-code.png',
      link: '/magic-binder'
    },
    {
      image: '/assets/features/mb-com-code.png',
      link: '/magic-binder-com'
    },
    {
      image: '/assets/features/pi-code.png',
      link: '/prod-ind'
    },
    {
      image: '/assets/features/willflix-code.png',
      link: '/willflix'
    }
  ];

  constructor(private renderer: Renderer2) { }

  ngOnInit(): void {
  }

  openModal() {
    this.renderer.setStyle(this.myModal.nativeElement, 'display', 'block');
    this.renderer.setProperty(this.video.nativeElement, 'src', this.videoUrl);
  }

  closeModal(event: any) {
    if (event.target == this.myModal.nativeElement) {
      this.close();
    }
  }

  close() {
    this.renderer.setStyle(this.myModal.nativeElement, 'display', 'none');
    this.renderer.setProperty(this.video.nativeElement, 'src', '');
  }

}
<div class="parent">
    <app-nav class="nav"></app-nav>
    <div class="main-right">
        <div class="section-one">
            <div class="main-text-wrap">
                <div class="w-series">
                    <div class="series">SHORT FILM</div>
                </div>
                <div class="series-title">Angel On My Shoulder</div>
                <div class="info-bar">
                    <div class="date">Spring 2023</div>
                    <div class="episodes">1 Episode</div>
                    <div class="tools">
                        <div class="tag director">
                            <img class="tag-icon" src="/assets/tags/dir.png"/>
                            <div class="tag-text">Dir. Ann-Corynn Rivière</div>
                        </div>
                        <div class="tag producer">
                            <img class="tag-icon" src="/assets/tags/prod.png"/>
                            <div class="tag-text">Prod. Madison White</div>
                        </div>
                    </div>
                </div>
                <div class="episode-title">Synopsis:</div>
                <div class="episode-description">
                    On the day of her final exam to become a guardian angel, a young girl figures out she is being sabotaged by one of her peers.
                </div>
                <button (click)="openModal()" class="play-button">
                    <div class="play"></div>
                    Play
                </button>
            </div>
        </div>
        <div class="bottom-gradient"></div>
    </div>
</div>
<app-footer></app-footer>

<!-- Modal content -->
<div #myModal class="modal" (click)="closeModal($event)">
    <div class="modal-content">
        <span class="close" (click)="closeModal()">&times;</span>
        <iframe #video src="" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>
    </div>
</div>
